function MovieItem({ movie }) {
  return (
    <li className="border rounded bg-white p-3 d-flex justify-content-between align-items-center mt-3">
      <div>
        <p className="m-0 fs-5">
          <b>
            {movie.title} ({movie.year})
          </b>
        </p>
        {/* Comment field (fallback to dash if missing) */}
        <p className="m-0 text-muted fst-italic">
          {movie.comment ?? '-'}
        </p>
      </div>

      <span className="badge text-bg-warning rounded-pill">
        <i className="bi bi-star-fill"></i> {movie.rating ?? '-'}
      </span>
    </li>
  );
}

export default MovieItem;
