import { useParams, Link } from 'react-router-dom';

function MovieDetails({ movies, reviews, setReviews }) {
  const { id } = useParams();
  const movie = movies.find(m => m.id.toString() === id);

  if (!movie) {
    return (
      <div>
        <h2>Movie not found</h2>
        <Link to="/">‚Üê Back to search</Link>
      </div>
    );
  }

  return (
    <div className="row justify-content-center">
      <div className="col-lg-6 bg-light-subtle border rounded pb-4">
        <Link to="/" className="text-dark text-decoration-none">
          <div className="mt-2" style={{ fontSize: "16px" }}>
            <i className="bi bi-arrow-left-circle"></i> Go back to search
          </div>
        </Link>

        <hr className="border-secondary-subtle" />

        <h2 className="mt-2">
          <b>{movie.title}</b>
        </h2>
        <p>Released: {movie.year}</p>

        <hr className="border-secondary-subtle" />

        <div className="d-flex justify-content-between align-items-center">
          <h6 className="m-0"><b>Reviews</b></h6>

          <div>
            <i class="bi bi-plus-circle"></i> New review
          </div>
        </div>

        <p className="text-center">
          No reviews
        </p>

