import { useParams, Link } from 'react-router-dom';
import ReviewItem from './ReviewItem';

function MovieDetails({ movies, reviews, setReviews }) {
  const { id } = useParams();
  const movie = movies.find(m => m.id.toString() === id);

  if (!movie) {
    return (
      <div>
        <h2>Movie not found</h2>
        <Link to="/">‚Üê Back to search</Link>
      </div>
    );
  }

  return (
    <div className="row justify-content-center">
      <div className="col-lg-6 bg-light-subtle border rounded pb-4">
        <Link to="/" className="text-dark text-decoration-none">
          <div className="d-flex justify-content-start align-items-center gap-2">
            <i class="bi bi-arrow-left-circle"></i> <p className="m-0" style={{ fontSize: "14px" }}>Go back to search</p>
          </div>
        </Link>

        <hr className="border-secondary-subtle" />

        <h2 className="mt-2">
          <b>{movie.title}</b>
        </h2>
        <p>Released: {movie.year}</p>

        <hr className="border-secondary-subtle" />

        <div className="d-flex justify-content-between align-items-center">
          <h6 className="m-0"><b>Reviews</b></h6>

          <div className="d-flex justify-content-between align-items-center gap-2">
            <i class="bi bi-plus-circle"></i> <p className="m-0" style={{ fontSize: "14px" }}>New review</p>
          </div>
        </div>

        <div class="modal fade" id="staticBackdrop" data-bs-backdrop="static" data-bs-keyboard="false" tabindex="-1" aria-labelledby="staticBackdropLabel" aria-hidden="true">
          <div class="modal-dialog">
            <div class="modal-content">
              <div class="modal-header">
                <h1 class="modal-title fs-5" id="staticBackdropLabel">Modal title</h1>
                <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
              </div>
              <div class="modal-body">
                ...
              </div>
              <div class="modal-footer">
                <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                <button type="button" class="btn btn-primary">Understood</button>
              </div>
            </div>
          </div>
        </div>

        { /* TODO: Move to component */ }
        <ul className="p-0 m-0">
          {reviews.length > 0 ? (
            reviews.map((review, index) => <ReviewItem key={index} review={review} />)
          ) : (
            <li className="list-group-item text-center mt-4">No reviews</li>
          )}
        </ul>
      </div>
    </div>
  );
}

export default MovieDetails;
