import { useState, useEffect } from 'react';
import './App.css';
import MovieList from './components/MovieList';
import SearchBar from './components/SearchBar';

function App() {
  const [searchTerm, setSearchTerm] = useState("");
  const [filteredMovies, setFilteredMovies] = useState([]);
  const [loading, setLoading] = useState(false);

  const movies = [
    { title: 'The Shawshank Redemption', year: 1994, comment: null, rating: null },
    { title: 'The Godfather', year: 1972, comment: null, rating: null },
    { title: 'The Dark Knight', year: 2008, comment: null, rating: null },
    { title: 'Pulp Fiction', year: 1994, comment: null, rating: null },
    { title: 'Forrest Gump', year: 1994, comment: null, rating: null },
    { title: 'Inception', year: 2010, comment: null, rating: null },
    { title: 'Interstellar', year: 2014, comment: null, rating: null },
    { title: 'Parasite', year: 2019, comment: null, rating: null },
  ];

  useEffect(() => {
    if (!searchTerm) {
      setFilteredMovies([]);
      setLoading(false);
      return;
    }

    setLoading(true);

    const timer = setTimeout(() => {
      const filtered = movies.filter(movie =>
        movie.title.toLowerCase().includes(searchTerm.toLowerCase())
      );

      setFilteredMovies(filtered);
      setLoading(false);
    }, 1000);

    return () => clearTimeout(timer);
  }, [searchTerm]);

  const clearInput = () => {
    setSearchTerm("");
    setFilteredMovies([]);
    setLoading(false);
  };

  return (
    <div className="container p-4">
      <div className="row justify-content-center">
        <div className="col-lg-6 bg-light-subtle border rounded pt-3 pb-3">

          <SearchBar
            searchTerm={searchTerm}
            onSearchChange={setSearchTerm}
            onClear={clearInput}
          />

          <MovieList 
            movies={filteredMovies} 
            loading={loading} 
            searchTerm={searchTerm} 
          />

        </div>
      </div>
    </div>
  );
}

export default App;
